    const changeStyle = () => {
        console.log("you just clicked");

        setStyle("App-header2");
      }
    }

export const JjDeDaGg = () => {

    const [number, setNumber] = useState(0) //this is an react hook

  return (


    <div>Stop the dizzy
        <div><button onClick={() => setNumber(number+1)}>Spin the other way</button>

        <button className="button" onClick={changeStyle}>
          Click me!
        </button>


        <div onMouseMove = {() => setNumber(number+1)}> 
            {number}
            </div>
        </div>
    </div>

  )
}


